import React, { useState, useEffect } from 'react';
import { Grid, Paper, TextField, Button, Typography } from "@material-ui/core";
import { Link, useNavigate } from "react-router-dom";
import axios from "axios";

const Login = () => {
  const paperStyle = { padding: 50, width: 300, margin: "150px auto" };
  const navigate = useNavigate();

  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [res, setResponse] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get("http://localhost:8080/users"); // Replace with your actual login endpoint
        console.log("response", response.data);
        setResponse(response.data);
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    }

    fetchData();
  }, []);

  const SubmitForm = (e) => {
    e.preventDefault();

    if (!email || !password) {
      alert("Please fill in all fields");
      return;
    }

    if (res.length > 0) {
      const user = res.find(user => user.email === email && user.password === password);

      if (user) {
        console.log("You Are Successfully Logged In");
        navigate("/dashboard"); // Replace with your actual dashboard route
      } else {
        alert("Email or Password is not matching with our records");
      }
    } else {
      alert("No users found. Please sign up.");
    }
  }

  return (
    <Grid container="center" style={{ backgroundColor: "pink", height: "100vh" }}>
      <Paper elevation={10} style={paperStyle}>
        <form>
          <Typography variant="h5">Login</Typography>
          <TextField label="Email" placeholder="Enter Email" fullWidth required margin="normal" onChange={(e) => setEmail(e.target.value)} value={email} autoComplete="off" />
          <TextField
            label="Password"
            placeholder="Enter Password"
            type="password"
            fullWidth
            required
            margin="normal"
            onChange={(e) => setPassword(e.target.value)} value={password}
          />
          <Button type="submit" variant="contained" color="primary" fullWidth onClick={SubmitForm}>
            Login
          </Button>
        </form>
        <Typography variant="body2" style={{ marginTop: 10 }}>
          Don't have an account?
          <Link to='/signup'>Sign up</Link>
        </Typography>
      </Paper>
    </Grid>
  );
};

export default Login;
